window.onload=function(){var e=getid("btn"),t=getid("psw"),n=getid("tex");e.onclick=function(){var i=n.value.trim(),a=t.value.trim();if(i&&a){var e="APItype=login&username="+i+"&password="+a+"&time="+new Date;ajax("POST","../api/reg.php",e,function(e){if(console.log(e),"1"==e){var t=new Date;t.setDate(t.getDate()+7),cookie.set("usn",i,{expries:t}),cookie.set("psw",a,{expries:t}),location.href="index.html"}else alert("登录失败")})}else alert("信息错误")}};